package main

import "fmt"

func countAndSay(n int) string {
	s := "1$"
	for i := 1; i < n; i++ {
		count := 1
		temp := ""
		for j := 0; j < len(s)-1; j++ {
			if s[j+1] == s[j] {
				count++
			} else {
				temp += fmt.Sprint(count) + string(s[j])
				count = 1
			}
		}
		s = temp + "$"
	}
	return s[:len(s)-1]
}

